\c cars_data;
DROP TABLE IF EXISTS car_specs;
CREATE TABLE car_specs (id SERIAL PRIMARY KEY,model VARCHAR(84),serie VARCHAR(42),company VARCHAR(26),body_style VARCHAR(67),segment VARCHAR(24),production_years TEXT,cylinders VARCHAR(79),displacement VARCHAR(12),power_hp VARCHAR(27),power_bhp VARCHAR(28),power_kw VARCHAR(29),torque_lb_ft VARCHAR(30),torque_nm VARCHAR(27),electrical_motor_power VARCHAR(21),electrical_motor_torque VARCHAR(24),fuel_system VARCHAR(198),fuel VARCHAR(31),fuel_capacity VARCHAR(25),top_speed VARCHAR(29),acceleration_0_62 VARCHAR(13),drive_type VARCHAR(19),gearbox VARCHAR(154),front_brake VARCHAR(91),rear_brake VARCHAR(91),tire_size VARCHAR(66),car_length VARCHAR(35),car_width VARCHAR(26),car_height VARCHAR(26),front_rear_track VARCHAR(35),wheelbase VARCHAR(35),ground_clearance VARCHAR(29),aerodynamics_cd VARCHAR(11),aerodynamics_frontal_area VARCHAR(9),turning_circle VARCHAR(20),cargo_volume VARCHAR(28),unladen_weight VARCHAR(36),gross_weight_limit VARCHAR(36),combined_mpg VARCHAR(29),city_mpg VARCHAR(29),highway_mpg VARCHAR(29),co2_emissions VARCHAR(66),co2_emissions_combined VARCHAR(10),turning_circle_curb_to_curb VARCHAR(19),total_max_power VARCHAR(25),power_pack VARCHAR(67),nominal_capacity VARCHAR(11),top_speed_electrical VARCHAR(20),ev_range VARCHAR(26),high_mpg VARCHAR(28),extra_high_mpg VARCHAR(28),medium_mpg VARCHAR(28),low_mpg VARCHAR(28),total_max_torque VARCHAR(24),max_capacity VARCHAR(11),spec_summary VARCHAR(60));
GRANT SELECT ON car_specs TO pg;