\c cpd_traffic_crashes_db;

DROP TABLE IF EXISTS cpd_traffic_crashes;
CREATE TABLE IF NOT EXISTS cpd_traffic_crashes (crash_record_id INTEGER PRIMARY KEY NOT NULL,crash_date_est_i VARCHAR,crash_date VARCHAR,posted_speed_limit INTEGER,traffic_control_device VARCHAR,device_condition VARCHAR,weather_condition VARCHAR,lighting_condition VARCHAR,first_crash_type VARCHAR,trafficway_type VARCHAR,lane_cnt INTEGER,alignment VARCHAR,roadway_surface_cond VARCHAR,road_defect VARCHAR,report_type VARCHAR,crash_type VARCHAR,intersection_related_i VARCHAR,not_right_of_way_i VARCHAR,hit_and_run_i VARCHAR,damage VARCHAR,date_police_notified VARCHAR,prim_contributory_cause VARCHAR,sec_contributory_cause VARCHAR,street_num INTEGER,street_direction VARCHAR,street_name VARCHAR,beat_of_occurence INTEGER,photos_taken_i VARCHAR,statements_taken_i VARCHAR,dooring_i VARCHAR,work_zone_i VARCHAR,work_zone_type VARCHAR,workers_present_i VARCHAR,num_units INTEGER,most_severe_injury VARCHAR,injuries_total INTEGER,injuries_fatal INTEGER,injuries_incapacitating INTEGER,injuries_non_incapacitating INTEGER,injuries_reported_not_evident INTEGER,injuries_no_indication INTEGER,injuries_unknown INTEGER,crash_hour INTEGER,crash_day_of_week INTEGER,crash_month INTEGER,latitude FLOAT,longitude FLOAT,crash_location VARCHAR);
GRANT SELECT ON cpd_traffic_crashes TO pg;
